const inputText = $json["raw_output"] || $json["output"];

// Extraemos el bloque JSON delimitado por ```json ... ```
const jsonMatch = inputText.match(/```json([\s\S]*?)```/);

let result = {};

if (jsonMatch && jsonMatch[1]) {
  let jsonText = jsonMatch[1].trim();

  // Limpiar posibles comas finales antes de cerrar } o ]
  jsonText = jsonText.replace(/,\s*([\]}])/g, '$1');

  try {
    // Parseamos el JSON limpio
    const chatbotJSON = JSON.parse(jsonText);
    result = chatbotJSON;
  } catch (err) {
    console.warn("JSON.parse fall√≥, usando extracci√≥n por regex como fallback.");

    // Regex para capturar "clave": valor simple
    const regex = /"([^"]+)"\s*:\s*(true|false|null|"(?:[^"\\]|\\.)*"|\[[^\]]*\]|\{[^\}]*\}|-?\d+(\.\d+)?)/gi;
    let match;
    while ((match = regex.exec(jsonText)) !== null) {
      let key = match[1];
      let value = match[2].trim();

      // Convertimos valores a su tipo real
      if (value === "true") value = true;
      else if (value === "false") value = false;
      else if (value === "null") value = null;
      else if (/^\d+(\.\d+)?$/.test(value)) value = Number(value);
      else if (/^\[.*\]$/.test(value)) {
        try { value = JSON.parse(value); } catch { value = value; }
      }
      else if (/^".*"$/.test(value)) {
        value = value.slice(1, -1); // quitamos comillas
      }

      result[key] = value;
    }
  }
}

// Retornamos todas las variables al siguiente nodo
return [
  {
    json: result
  }
];

