      notifications_enabled: NOTIFICATIONS_ENABLED,
      platform: PLATFORM,
      created_at: CREATED_AT
    })
  }).then(res=>res.json())
    .then(data => { typingIndicator.remove(); addMessage('Bot', data.reply || OFFLINE_MSG, true); })
    .catch(()=>{ typingIndicator.remove(); addMessage('Bot', OFFLINE_MSG, true); });
}

function addMessage(sender,text,isBot=true){
  const msg=document.createElement('div');
  msg.classList.add('msg',isBot?'bot-msg':'user-msg');
  msg.innerHTML='<strong>'+(isBot?'Bot':sender)+':</strong> '+text;
  messages.appendChild(msg);
  messages.scrollTop=messages.scrollHeight;
}
